import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

import Form from "./components/Form";
import Name from "./components/Name";
import Contact from "./components/Contact";
import Experience from "./components/Experience";
import Skills from "./components/Skills";
import Education from "./components/Education";
import Awards from "./components/Awards";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      name: "",
      contact: [], // {phonenumber: "", email: "", link1:"", link2:""}
      experience: [], // {[ [position: "", company: "", city: "", state: "", startDate: "", endDate: "", resposibilities: ["", "", ...]], ...]}
      skills: [], // {[ [heading: "", skills["skill1", "skill2",..]], ...]}
      education: [], // {[[institution: "", graduationDate: "", description: "", city: "", state: ""], ...]}
      awards: [], // {[ [name: "", institution: "", date: ""], ...]}
    };

    this.setName = this.editName.bind(this);
    this.setContact = this.editContact.bind(this);
    this.setExperience = this.editExperience.bind(this);
    this.setSkills = this.editSkills.bind(this);
    this.setEducation = this.editEducation.bind(this);
    this.setAwards = this.editAwards.bind(this);
  }

  editName(e) {
    this.setState = {
      name: e.target.value,
      contact: this.state.contact,
      experience: this.state.experience,
      skills: this.state.skills,
      education: this.state.education,
      awards: this.state.awards
    };
  }

  editContact(e) {
    this.setState = {
      name: this.state.name,
      contact: e.target.value,
      experience: this.state.experience,
      skills: this.state.skills,
      education: this.state.education,
      awards: this.state.awards
    };
  }

  editExperience(e) {
    this.setState = {
      name: this.state.name,
      contact: this.state.contact,
      experience: e.target.value,
      skills: this.state.skills,
      education: this.state.education,
      awards: this.state.awards
    };
  }

  editSkills(e) {
    this.setState = {
      name: this.state.name,
      contact: this.state.contact,
      experience: this.state.experience,
      skills: e.target.value,
      education: this.state.education,
      awards: this.state.awards
    };
  }

  editEducation(e) {
    this.setState = {
      name: this.state.name,
      contact: this.state.contact,
      experience: this.state.experience,
      skills: this.state.skills,
      education: e.target.value,
      awards: this.state.awards
    };
  }

  editAwards(e) {
    this.setState = {
      name: this.state.name,
      contact: this.state.contact,
      experience: this.state.experience,
      skills: this.state.skills,
      education: this.state.education,
      awards: e.target.value
    };
  }

  render() {
    return (
      <div>
        {/* Input */}
        <Form />

        {/* Output */}
        <Name name= "this.state.name" setName= "this.setName"/>
        <Contact contact= "this.state.contact" setContact= "this.setContact"/>
        <Experience experience= "this.state.experience" setExperience= "this.setExperience"/>
        <Skills skills= "this.state.skills" setSkills= "this.setSkills" />
        <Education education= "this.state.education" setEducation="this.setEducation"/>
        <Awards awards="this.state.awards" setAwards= "this.setAwards" />
      </div>
    );
  }
}

export default App;
