import React, { Component } from "react";

//{[ [id: "", position: "", company: "", city: "", state: "", startDate: "", endDate: "", resposibilities: ["", "", ...]], ...]}
class Experience extends Component {
  constructor(props) {
    super(props);

    this.state = { responsibilities: [] };

    this.addResponsibilities = (e) => {
      e.preventDefault();
      console.log(e.target.responsibilities.value);
      e.target.reset();
      //this.setState([...this.state.responsibilities, e.target.responsibilities.value])
    }
  }

  render() {
    return (
      <div>
        <h4>Experience</h4>
        <form onSubmit={this.props.setExperience}>
          <label>
            Position :
            <input name="position" type="text" />
          </label>
          <label>
            Company :
            <input name="company" type="text" />
          </label>
          <label>
            City :
            <input name="city" type="text" />
          </label>
          <label>
            State :
            <input name="state" type="text" />
          </label>
          <label>
            Start Date :
            <input name="startDate" type="text" />
          </label>
          <label>
            End Date :
            <input name="endDate" type="text" />
          </label>
          <form onSubmit={this.addResponsibilities}>
          <label>
            Resposibilities :
            <input name="responsibilities" type="text" />
          </label>
          <button type="submit">+</button>
          </form>
          <div>
            <button type="submit">Add</button>
          </div>
        </form>
      </div>
    );
  }
}

export default Experience;
