import React, { Component } from "react";

//awards: [], // {[ [id: "", name: "", institution: "", date: ""], ...]}
class Awards extends Component {
  constructor(props) {
    super(props);

    //this.state = { isShowing: false };
    this.isShowing = false;
  }

  addPipeline(name, institution) {
    if (
      name !== undefined &&
      name !== "" &&
      institution !== null &&
      institution !== ""
    )
      return true;
    return false;
  }

  render() {
    return (
      <ul>
        {this.props.awards.map((award) => {
          return (
            <li
              key={award.id}
              onMouseEnter={() => {
                this.isShowing = true;
                //this.setState({ isShowing: true });
              }}
              onMouseLeave={() => {
                this.isShowing = false;
                //this.setState({ isShowing: false });
              }}
            >
              {award.name}

              {this.addPipeline(award.name, award.institution) && <> | </>}

              {award.institution}
              <> </>
              {award.date}
              {this.isShowing && <button name={award.id} onClick={(e) => this.props.deleteAwards(e)}>-</button>}
            </li>
          );
        })}
      </ul>
    );
  }
}

export default Awards;
