import React, { Component } from "react";

class Name extends Component {
  constructor(props) {
    super(props);

    this.state = { hasName: false };
  }

  render() {
    return (
      <div>
        <h4>Name</h4>
        <form onSubmit={this.props.setName}>
          <label>
            Name :
            <input name="name" type="text" />
          </label>
          <button type="submit">
            {!this.state.hasName && <>Add</>}
            {this.state.hasName && <>Update</>}
          </button>
        </form>
      </div>
    );
  }
}

export default Name;
