import React, { Component } from "react";

class Skills extends Component {
  constructor(props) {
    super(props);

    this.state = { isShowing: false };

    this.onClickEvent = () => {
      this.props.deleteSkill();
      this.setState({ isShowing: false });
    };
  }

  render() {
    return (
      <div>
        <ul>
          {this.props.skills.map((skill) => {
            return (
              <li>
                {skill.heading}
                {skill.skills}
              </li>
            );
          })}
        </ul>
      </div>
    );
  }
}

export default Skills;
