import React, { Component } from "react";

class Name extends Component {
  constructor(props) {
    super(props);

    this.state = { isShowing: false };
  }

  render() {
    return (
      <div
        onMouseEnter={() => {
          this.setState({ isShowing: true });
        }}
        onMouseLeave={() => {
          this.setState({ isShowing: false });
        }}
      >
        {this.props.name}
        {isShowing && <button>-</button>}
      </div>
    );
  }
}

export default Name;
